const loadJsonFile = require('load-json-file');
const mysql = require('mysql');

exports.Connection_create = function() {

  var securityConfig = loadJsonFile.sync('config/security.local.json');
  
  var connection = mysql.createConnection({
    host: securityConfig.database.host,
    user: securityConfig.database.user,
    password: securityConfig.database.password,
    database: securityConfig.database.database
  });
  
  connection.connect(function(err) {
    if (err) throw err;
  });
  
  return connection;
}