var fs = require('fs')
const https = require('https')
const loadJsonFile = require('load-json-file');

exports.create = function(app) {

  var securityConfig = loadJsonFile.sync('config/security.local.json');

  return https.createServer({
    key: fs.readFileSync(securityConfig.server.key),
    cert: fs.readFileSync(securityConfig.server.certificate)
  }, app)
}