const placeSet = [
{ 
  "id": 1,
  "instance_id": 1,
  "caption": "Place 1"
},
{ 
  "id": 2,
  "instance_id": 2,
  "caption": "Place 2"
}];

/**
 * Get a single row based on a possibly composite key
 */
exports.select = function (context, connection, table, where, { columns = null , orderedBy = null, joins = null } = {}) 
{
  return new Promise ( (resolve, reject) => {

    // Serialize where predicates
    where.serialize();
    rows = placeSet;
    resolve(rows);
  });
}

